INSERT INTO `quillquestdb` (Name, Fandom, Ship(s), Rating, Link, Review, Abstract) VALUES ('"Kim Commidashian"', '"Kardashians"', '"None"', '13', '"https://archiveofourown.org/works/34002106"', '"Oh Godâ€¦"', '"Kim decides to experiment with communism. Join her with this heartfelt story as she leaves her rich life for the life of a poor communist in NYC."');
